import{_ as e,c as s,o as a,j as t}from"./chunks/framework.SbOxrpL8.js";const o="/images/2007/04/04_12778.gif",_="/images/2007/04/04_200704051250177570_12753.gif",x=JSON.parse('{"title":"笔记本散热垫","description":"","frontmatter":{"title":"笔记本散热垫","tags":["笔记本"],"id":935,"categories":["Digital"],"abbrlink":"9dc1ddd7","date":"2007-04-04T20:38:39.000Z"},"headers":[],"relativePath":"Digital/笔记本散热垫.md","filePath":"Digital/笔记本散热垫.md","lastUpdated":1719711816000}'),i={name:"Digital/笔记本散热垫.md"},n=t("p",null,[t("img",{src:o,alt:"笔记本散热垫"})],-1),c=t("p",null,"一直考虑买个散热垫，之前是在笔记本下面垫2个冰红茶的盖子，把二奶垫高助于散热，但夏天就要到鸟，有点担心硬盘温度过高而影响寿命，整个笔记本最值钱的就是硬盘里的数据了",-1),d=t("p",null,"被动散热基本不用考虑了，主动散热的无非就是弄几个风扇吹风，低价的20块钱就可以买到，好一点的像游骑士什么的，都要百来块，无非是把塑料壳子换成铝合金、亚克力什么，更有的弄几个LED灯，加个USB Hub上去就卖到2、3百，真把消费者当白痴了。",-1),l=t("p",null,"跟笔记本一般大小的散热垫，感觉比较占空间，而且风扇进气口和出气口也有些讲究，大多出都是从中间吹风，可是笔记本的进风口普遍不是在中间的，再者，散热垫进气口只能从周围桌面，这样散热垫周围如果灰尘多一点的，岂不都吸进笔记本了。",-1),r=t("p",null,"老猫在淘宝买了个散热垫，包快递33元，散热效果还对得起这价钱，就是有点嗡嗡的噪音，还好可以接受，而且老猫经常戴耳机的，就更无所谓了，分别监控CPU和硬盘温度，使用之前分别为78℃、55℃，使用之后温度明显下降，如下图，图里每格为10分钟。",-1),p=t("p",null,"其实这东西真可说是没啥技术含量，有条件的自己买个风扇和亚克力就可以DIY一个了。",-1),m=t("p",null,[t("img",{src:_,alt:"笔记本散热垫"})],-1),g=[n,c,d,l,r,p,m];function u(h,f,D,b,B,$){return a(),s("div",{"data-pagefind-body":!0},g)}const P=e(i,[["render",u]]);export{x as __pageData,P as default};
