import{_ as t,o as a,c as e,j as l,bE as s,bF as i}from"./chunks/framework.nrF9sM7m.js";const D=JSON.parse('{"title":"笔记本散热垫","description":"","frontmatter":{"title":"笔记本散热垫","tags":["笔记本"],"id":935,"categories":["Digital"],"abbrlink":"9dc1ddd7","date":"2007-04-04T20:38:39.000Z"},"headers":[],"relativePath":"Digital/笔记本散热垫.md","filePath":"Digital/笔记本散热垫.md","lastUpdated":1719711816000}'),o={name:"Digital/笔记本散热垫.md"},_=l("p",null,[l("img",{src:s,alt:"笔记本散热垫"})],-1),d=l("p",null,"一直考虑买个散热垫，之前是在笔记本下面垫2个冰红茶的盖子，把二奶垫高助于散热，但夏天就要到鸟，有点担心硬盘温度过高而影响寿命，整个笔记本最值钱的就是硬盘里的数据了",-1),n=l("p",null,"被动散热基本不用考虑了，主动散热的无非就是弄几个风扇吹风，低价的20块钱就可以买到，好一点的像游骑士什么的，都要百来块，无非是把塑料壳子换成铝合金、亚克力什么，更有的弄几个LED灯，加个USB Hub上去就卖到2、3百，真把消费者当白痴了。",-1),T=l("p",null,"跟笔记本一般大小的散热垫，感觉比较占空间，而且风扇进气口和出气口也有些讲究，大多出都是从中间吹风，可是笔记本的进风口普遍不是在中间的，再者，散热垫进气口只能从周围桌面，这样散热垫周围如果灰尘多一点的，岂不都吸进笔记本了。",-1),J=l("p",null,"老猫在淘宝买了个散热垫，包快递33元，散热效果还对得起这价钱，就是有点嗡嗡的噪音，还好可以接受，而且老猫经常戴耳机的，就更无所谓了，分别监控CPU和硬盘温度，使用之前分别为78℃、55℃，使用之后温度明显下降，如下图，图里每格为10分钟。",-1),c=l("p",null,"其实这东西真可说是没啥技术含量，有条件的自己买个风扇和亚克力就可以DIY一个了。",-1),U=l("p",null,[l("img",{src:i,alt:"笔记本散热垫"})],-1),r=[_,d,n,T,J,c,U];function M(p,I,y,m,R,h){return a(),e("div",{"data-pagefind-body":!0,"data-pagefind-meta":"base64:"},r)}const g=t(o,[["render",M]]);export{D as __pageData,g as default};
