import{_,o as s,c as p,a7 as t}from"./chunks/framework.Z1Dznsxj.js";const i="/images/2008/11/21_200811211653394733_10566.jpg",m="/images/2008/11/21_200811211654305608_10567.jpg",g="/images/2008/11/21_200811211654377721_10568.jpg",a="/images/2008/11/21_200811211654466380_10569.jpg",o="/images/2008/11/21_200811211655022441_10570.jpg",r="/images/2008/11/21_200811211655102321_10571.jpg",c="/images/2008/11/21_200811211656271455_10572.jpg",e="/images/2008/11/21_200811211656350274_10573.jpg",n="/images/2008/11/21_200811211657077562_10574.jpg",l="/images/2008/11/21_200811211657155088_10575.jpg",j="/images/2008/11/21_200811211659150073_10576.jpg",d="/images/2008/11/21_200811211659432045_10577.jpg",f="/images/2008/11/21_200811211659492682_10578.jpg",B="/images/2008/11/21_200811211700042550_10579.jpg",O="/images/2008/11/21_200811211700262270_10580.jpg",Y="/images/2008/11/21_200811211700344073_10581.jpg",T="/images/2008/11/21_200811211702247403_10582.jpg",h="/images/2008/11/21_200811211702364843_10583.jpg",S="/images/2008/11/21_200811211703054527_10584.jpg",u="/images/2008/11/21_200811211703125262_10585.jpg",A="/images/2008/11/21_200811211704366873_10586.jpg",L="/images/2008/11/21_200811211705342543_10587.jpg",P="/images/2008/11/21_200811211705462717_10588.jpg",$="/images/2008/11/21_200811211706163400_10589.jpg",b="/images/2008/11/21_200811211706230707_10590.jpg",k="/images/2008/11/21_200811211706315063_10591.jpg",x="/images/2008/11/21_200811211707094230_10592.jpg",N="/images/2008/11/21_200811211707183857_10593.jpg",V="/images/2008/11/21_200811211707416428_10594.jpg",v="/images/2008/11/21_200811211707510036_10595.jpg",C="/images/2008/11/21_200811211708108516_10596.jpg",D="/images/2008/11/21_200811211709171762_10597.jpg",E="/images/2008/11/21_200811211710157005_10598.jpg",G="/images/2008/11/21_200811211709432842_10599.jpg",I="/images/2008/11/21_200811211713461473_10600.jpg",M="/images/2008/11/21_200811211713565661_10601.jpg",Q=JSON.parse('{"title":"2008磨房@惠州60公里徒步","description":"","frontmatter":{"title":"2008磨房@惠州60公里徒步","tags":["惠州","磨房","单车"],"id":1060,"categories":["Life"],"abbrlink":"8733a705","date":"2008-11-17T14:39:46.000Z"},"headers":[],"relativePath":"Life/2008磨房_惠州60公里徒步.md","filePath":"Life/2008磨房_惠州60公里徒步.md","lastUpdated":1719711816000}'),y={name:"Life/2008磨房_惠州60公里徒步.md"},J=t('<p>11月16日是第六届惠州60公里徒步活动日，刚好前一周买了单车（我也是有车的人了，欧也），于是准备去凑热闹，跟BOYBOY报名了，报名的时候怕被拒绝，还不敢说没骑过那么远，然后就混进去了，欧也</p><p>15日凌晨4点半起来，习惯性50个俯卧撑后，嬷嬷唧唧半天，搞到5点12分才扛车下楼</p><p>5:16 出发，55分钟后抵达东门，停下来吃了2个面包，嘉顿的，挺松软，好吃，平均心跳144。然后一路骑到罗沙公路，原来就是罗湖体育馆那边，差不多七点，找到BOYBOY的店，还没开，有点饿，周围绕了2圈，没看到卖早餐的，平均心跳135</p><p><img src="'+i+'" alt=""> 7:05车店留影，整整在店门口坐了1小时，期间做了20个青蛙跳，然后发现前面保安在看我，=_=||</p><p>BOYBOY真准时，8点整出现，第一次见到传说中的BOYBOY，挺可爱的，刘亦菲有点像BOY</p><p>8:08出发，在仙湖上坡时，BOY语重心长的说，上坡时不要变速，很伤链条，铭记在心。一路上坡下坡加越野，上坡很爽，下坡手动ABS更是爽到手抽筋。</p><p><img src="'+m+'" alt=""> 不知哪的湖，趁着拍合影赶紧拍张走人</p><p><img src="'+g+'" alt=""></p><p><img src="'+a+'" alt=""> 惠阳，赶不上先锋队伍，肚子非常饿，干脆停下等后面队伍</p><p><img src="'+o+'" alt=""> 看到汕头，不知从这里回去还要多远</p><p><img src="'+r+'" alt=""> 下午2点半，继续挨饿中，踏频58，之前听说踏频可以更有效的锻炼，于是买了这表，可到手后发现没啥用，这次总算知道了，跟车子转速一个道理，让你合理换挡</p><p><img src="'+c+'" alt=""></p><p><img src="'+e+'" alt=""> 太好了，有饭吃了</p><p><img src="'+n+'" alt=""></p><p><img src="'+l+'" alt=""> 很好的GG，一路都护在MM旁边</p><p><img src="'+j+'" alt=""> 自拍，据说骑行者都是孤独的，我不孤独，我有二奶车</p><p><img src="'+d+'" alt=""></p><p><img src="'+f+'" alt=""></p><p><img src="'+B+'" alt=""></p><p><img src="'+O+'" alt=""> 路上折腾了7个半小时后（包括中途休息等人时间，不包括吃饭），抵达惠州，跟二奶住进旅馆，平均心跳149</p><p><img src="'+Y+'" alt=""> 从9楼拍摄落日</p><p><img src="'+T+'" alt=""></p><p><img src="'+h+'" alt=""></p><p><img src="'+S+'" alt=""></p><p><img src="'+u+'" alt=""></p><p><img src="'+A+'" alt=""> 第二天集合时合影</p><p><img src="'+L+'" alt=""> 路上遇到66，今年3月份的深圳百公里，我就是穿轮滑去的，不过这小孩怎么下坡？</p><p><img src="'+P+'" alt=""> 很美的风景</p><p><img src="'+$+'" alt=""> 一个大弯，路上人太多，不敢冲，一路刹下去</p><p><img src="'+b+'" alt=""> 不知什么湖，反正很美</p><p><img src="'+k+'" alt=""></p><p><img src="'+x+'" alt=""></p><p><img src="'+N+'" alt=""> 越野开始，照片上这路算好的了</p><p><img src="'+V+'" alt=""></p><p><img src="'+v+'" alt=""></p><p><img src="'+C+'" alt=""> 一路爬坡不断，偏偏前变出了问题，一路用2档爬坡，爬不上就下车推，更气的是错过第三签到点，却不知道以为快到了，导致没节制得喝水，后面有一小时基本没水喝，到了第四签到点后义工告知前面5公里有卖水，买了3瓶水吃了个玉米，好甜</p><p><img src="'+D+'" alt=""></p><p><img src="'+E+'" alt=""> 到终点了，然后上公路，午餐是鸵鸟大餐</p><p><img src="'+G+'" alt=""> 2天行程216公里，第一天135公里，第二天60公里，后来打黑车到布吉关口后骑回家的，再加上20公里</p><p><img src="'+I+'" alt=""></p><p><img src="'+M+'" alt=""></p>',41),R=[J];function U(Z,q,w,z,F,H){return s(),p("div",{"data-pagefind-body":!0},R)}const W=_(y,[["render",U]]);export{Q as __pageData,W as default};
