import{_,o as p,c as s,a7 as t}from"./chunks/framework.CmR4KQfH.js";const i="/images/2008/04/14_14_090723_9815.jpg",a="/images/2008/04/14_14_090723_0_9816.jpg",m="/images/2008/04/14_14_090723_1_9817.jpg",g="/images/2008/04/14_14_090723_2_9818.jpg",o="/images/2008/04/14_14_090723_3_9819.jpg",r="/images/2008/04/14_14_090723_4_9820.jpg",c="/images/2008/04/14_14_090723_5_9821.jpg",e="/images/2008/04/14_14_090723_6_9822.jpg",n="/images/2008/04/14_14_090723_7_9823.jpg",l="/images/2008/04/14_14_090723_8_9824.jpg",j="/images/2008/04/14_14_090723_9_9825.jpg",d="/images/2008/04/14_14_090723_10_9826.jpg",f="/images/2008/04/14_14_090723_11_9827.jpg",h="/images/2008/04/14_14_090723_12_9828.jpg",T="/images/2008/04/14_14_090723_13_9829.jpg",b="/images/2008/04/14_14_090723_14_9830.jpg",u="/images/2008/04/14_14_090723_15_9831.jpg",N="/images/2008/04/14_14_090723_16_9832.jpg",S="/images/2008/04/14_14_090723_17_9833.jpg",k="/images/2008/04/14_14_090723_18_9834.jpg",x="/images/2008/04/14_14_090723_19_9835.jpg",P="/images/2008/04/14_14_090723_20_9836.jpg",v="/images/2008/04/14_14_090723_21_9837.jpg",A="/images/2008/04/14_14_090723_23_9838.jpg",E="/images/2008/04/14_14_090723_24_9839.jpg",I="/images/2008/04/14_14_090723_25_9840.jpg",L="/images/2008/04/14_14_090723_26_9841.jpg",V="/images/2008/04/14_14_090723_27_9842.jpg",$="/images/2008/04/14_14_090723_28_9843.jpg",w="/images/2008/04/14_14_090723_29_9844.jpg",y="/images/2008/04/14_14_090723_30_9845.jpg",z="/images/2008/04/14_14_090723_31_9846.jpg",q="/images/2008/04/14_14_090723_32_9847.jpg",B="/images/2008/04/14_14_090723_33_9848.jpg",K=JSON.parse('{"title":"4月13日 梧桐山，刷上去又刷下来","description":"","frontmatter":{"title":"4月13日 梧桐山，刷上去又刷下来","tags":["刷街","户外","梧桐山","轮滑"],"id":1017,"categories":["Life"],"abbrlink":"d9682b67","date":"2008-04-14T09:07:23.000Z"},"headers":[],"relativePath":"Life/4月13日梧桐山.md","filePath":"Life/4月13日梧桐山.md","lastUpdated":1719711816000}'),C={name:"Life/4月13日梧桐山.md"},D=t('<p>参加：猪头、猫、艾文</p><p>艾文迟到15分钟，批评下 猪头不戴护具，不批评了，他已经得到血的教训了</p><p>之前一周都是吃素，体能不是很好，加上上周跑了20公里，右脚有点痛，不是担心刷不上去，是担心死撑会留下后患 不过看来之前的担心是多余的，比起之前大梅沙那次刷山（磨房百公里），那还真不是一个级别 强度没大梅沙那次大，上山没怎么累，下山没那么陡，参加过大梅沙的都可以很轻松刷上梧桐山</p><h3 id="上山1小时43分-下山26分钟" tabindex="-1">上山1小时43分，下山26分钟 <a class="header-anchor" href="#上山1小时43分-下山26分钟" aria-label="Permalink to &quot;上山1小时43分，下山26分钟&quot;">​</a></h3><p>下过小雨，地面都是湿的，轮子一直打滑，半山还下起小小雨，一路雾很大，可见度很低，很担心下不来，因为刹不住 一直担心那路面下不来，雾又很大，还好中午过后就开始有点太阳，不过路还是很滑，而且车多人多，路又窄，没法S刹，又不会双刹，只好一路T下来</p><p>到了山上大概12点，衣服都湿了，风很大，好冷，吃过午餐之后决定不顾天气，马上下山，还好没多久出了点太阳，雾渐渐散去，路也开始变干 到山上的时候很快乐，又一次战胜了自己，发了条短信和某人分享，下山后却有点沮丧，因为比起刷山，有些事，不是努力之后就有结果的。</p><p>梧桐山北大门出发，到好汉坡停车场 本来是艾文最先到的，可惜他走错路，跑到小梧桐去了，所以我跟猪头是最先到的</p><p><embed src="//player.56.com/v_MzI3NjIzNzA.swf" type="application/x-shockwave-flash" width="480" height="395"></p><p>0:07 上山中，雾很大 0:53 大小梧桐分岔口的水泥路，有点下坡，很爽 1:45 好汉坡停车场，到了！ 2:19 下山了，已到北大门</p><p><img src="'+i+'" alt=""> 北大门下出发</p><p><img src="'+a+'" alt=""></p><p><img src="'+m+'" alt=""></p><p><img src="'+g+'" alt=""></p><p><img src="'+o+'" alt=""></p><p><img src="'+r+'" alt=""></p><p><img src="'+c+'" alt=""> 这里是取山泉的地方</p><p><img src="'+e+'" alt=""></p><p><img src="'+n+'" alt=""></p><p><img src="'+l+'" alt=""></p><p><img src="'+j+'" alt=""></p><p><img src="'+d+'" alt=""> 山上雾很大，地面都是湿的</p><p><img src="'+f+'" alt=""></p><p><img src="'+h+'" alt=""></p><p><img src="'+T+'" alt=""></p><p><img src="'+b+'" alt=""> 大小梧桐的分岔口，这里有段路很好刷</p><p><img src="'+u+'" alt=""></p><p><img src="'+N+'" alt=""> 灰蒙蒙一片，能见度很低</p><p><img src="'+S+'" alt=""> 南山66，No.1</p><p><img src="'+k+'" alt=""> 到了到了，不容易啊</p><p><img src="'+x+'" alt=""> 发短信报喜</p><p><img src="'+P+'" alt=""> E3超人</p><p><img src="'+v+'" alt=""></p><p><img src="'+A+'" alt=""> 中午过后雾开始退了</p><p><img src="'+E+'" alt=""></p><p><img src="'+I+'" alt=""> 下坡中</p><p><img src="'+L+'" alt=""></p><p><img src="'+V+'" alt=""> 我下山前的轮子</p><p><img src="'+$+'" alt=""> 下山后的轮子，有一个已经OVER了</p><p><img src="'+w+'" alt=""> 猪头的鞋子</p><p><img src="'+y+'" alt=""> 轮子完全废了</p><p><img src="'+z+'" alt=""></p><p><img src="'+q+'" alt=""> 突然看到有个强悍的小孩往山上冲，冲了不到10米就下来了，原来他只是在门口玩玩下坡</p><p><img src="'+B+'" alt=""> 回来路过小平画像，有群双节棍爱好者，场面很酷</p>',43),O=[D];function R(J,M,U,Z,F,G){return p(),s("div",{"data-pagefind-body":!0},O)}const Q=_(C,[["render",R]]);export{K as __pageData,Q as default};
