import{_ as o,D as e,c as a,o as l,j as p,I as t,a7 as c,w as i,a as _}from"./chunks/framework.SbOxrpL8.js";const r="/images/2007/09/23_171001_13022.jpg",m="/images/2007/09/23_171042_13023.jpg",g="/images/2007/09/23_171051_13024.jpg",n="/images/2007/09/23_171107_13025.jpg",d="/images/2007/09/23_171114_13026.jpg",u="/images/2007/09/23_171120_13027.jpg",h="/images/2007/09/23_171141_13028.jpg",j="/images/2007/09/23_171153_13029.jpg",f="/images/2007/09/23_171202_13030.jpg",b="/images/2007/09/23_171209_13031.jpg",T="/images/2007/09/23_171225_13032.jpg",k="/images/2007/09/23_171231_13033.jpg",w="/images/2007/09/23_171239_13034.jpg",y="/images/2007/09/23_171246_13035.jpg",x="/images/2007/09/23_171253_13036.jpg",v="/images/2007/09/23_171300_13037.jpg",N="/images/2007/09/23_171316_13038.jpg",V="/images/2007/09/23_171324_13039.jpg",C="/images/2007/09/23_171333_13040.jpg",D="/images/2007/09/23_171417_13041.jpg",P="/images/2007/09/23_171516_13042.jpg",S="/images/2007/09/23_171523_13043.jpg",L="/images/2007/09/23_171530_12774.jpg",O="/images/2007/09/23_171630_13044.jpg",$="/images/2007/09/23_171649_13045.jpg",A="/images/2007/09/23_171656_13046.jpg",B="/images/2007/09/23_171717_13047.jpg",I="/images/2007/09/23_171724_13048.jpg",M="/images/2007/09/23_171732_13049.jpg",op=JSON.parse('{"title":"9月22日周六长刷到海滨广场","description":"","frontmatter":{"title":"9月22日周六长刷到海滨广场","tags":["刷街","南山66","市民中心","海滨广场","红树林","轮滑"],"id":969,"categories":["Life"],"abbrlink":"e3aec832","date":"2007-09-23T14:20:56.000Z"},"headers":[],"relativePath":"Life/9月22日周六长刷到海滨广场.md","filePath":"Life/9月22日周六长刷到海滨广场.md","lastUpdated":1719711816000}'),Q={name:"Life/9月22日周六长刷到海滨广场.md"},q=p("p",null,"时间：9月22日周六下午",-1),E=p("p",null,"人员：天天、树青、艾文、猫、安安、草草、坤坤、心灵驿站、吖牛、叶子、贝贝、蝌蚪、fly、游鱼、菜菜、小菜菜、飘飘、糖糖、小黄。。。（还有几位不知道怎么称呼的）",-1),z=p("p",null,"白石洲出发的有天天、树青、艾文、猫、安安、草草、坤坤、心灵驿站、吖牛，先是走深南大道刷去市民中心，花了不到1小时，在市民中心会合叶子，然后走滨海大道刷去科技园，到红树林段的时候跑到海边去了，刷了一会就遇到边防执勤人员，被迫走了一段沙路。",-1),F=p("p",null,"然后到科技园KFC会合贝贝、蝌蚪和fly等人，刷去宝安区交通局，然后菜菜和小菜菜也来了，一大票人刷去海滨广场。",-1),J=p("p",null,"树青因途中被铁皮割伤手，被迫退出，前往医院包扎。",-1),K=p("p",null,"在海滨广场活动到11点，又去腐败了，糖水加烧烤，然后刷回家。",-1),R=p("li",null,"蝌蚪，17公里",-1),U=p("li",null,"菜菜，12公里",-1),Z=p("li",null,"贝贝，10公里",-1),G=p("h3",{id:"总结",tabindex:"-1"},[_("总结 "),p("a",{class:"header-anchor",href:"#总结","aria-label":'Permalink to "总结"'},"​")],-1),H=c('<p>这次活动在白石洲集合的人全部非常准时，在刷街时候的纪律也非常好，使得之前预计4点到达市民中心，也准时到达了，叶子3:30就到那里等我们了，大热天的表扬一下。</p><p>走滨海大道去科技园的时候，因执勤人员干涉，被迫走了一大段砂石路，导致到科技园的时间比预计迟了13分钟，然后等人又拖了十分钟。。。。</p><p>预计到宝安区交通局时间是7:30，实际到达时间是7:46，然后又是等人。。。。。</p><p>另外在过一些路口的时候，希望前面的能等下后面的，这次活动前面的改了路线，我在后面的不知道就按老路线走了，</p><p>树青下楼梯被铁片割伤手，回来的时候在过南头检查站天桥的时候，天天又被该死的楼梯弄伤手指</p><p>发点牢骚：</p><blockquote><p>在送树青上的士之后，我顾不得其他人，就一个人刷去海滨广场了，很多人都在猜我晕血，其实我心情是很沉重的，我真的不希望看到有人受伤，特别是我组织的活动，所以我每次活动前都尽量考虑周全点，创可贴我已经用了13贴了，但都是给别人用的，**希望不要把我的话当耳边风，很多事故都是因为疏忽大意造成的。**不说了，说多就真成牢骚了。</p></blockquote><hr><p><embed src="//player.56.com/v_MTkyODQwOTQ.swf" type="application/x-shockwave-flash" width="480" height="395"></p><p>35公里刷街视频</p><p><embed src="//player.56.com/v_MTkyODQ1NzM.swf" type="application/x-shockwave-flash" width="480" height="395"></p><p>宝安海滨广场拉龙</p><p><img src="'+r+'" alt=""></p><p>在侨城东</p><p><img src="'+m+'" alt=""></p><p><img src="'+g+'" alt=""></p><p>来到深航大厦了</p><p><img src="'+n+'" alt=""></p><p><img src="'+d+'" alt=""></p><p><img src="'+u+'" alt=""></p><p>新人树青，体力蛮不错的</p><p><img src="'+h+'" alt=""></p><p><img src="'+j+'" alt=""></p><p><img src="'+f+'" alt=""></p><p><img src="'+b+'" alt=""></p><p>到达市民中心了</p><p><img src="'+T+'" alt=""></p><p><img src="'+k+'" alt=""></p><p><img src="'+w+'" alt=""></p><p><img src="'+y+'" alt=""></p><p><img src="'+x+'" alt=""></p><p><img src="'+v+'" alt=""></p><p><img src="'+N+'" alt=""></p><p><img src="'+V+'" alt=""></p><p>叶子在太阳下暴晒半小时，脸已经红了</p><p><img src="'+C+'" alt=""></p><p>遇到从坂田刷来的溜友，他们要去蛇口</p><p><img src="'+D+'" alt=""></p><p>在滨海大道吃哈密瓜，吖牛买单，我代大家谢谢了！</p><p><img src="'+P+'" alt=""></p><p><img src="'+S+'" alt=""></p><p><img src="'+L+'" alt=""></p><p>在红树林段，没有楼梯，直接从2米高的墙上跳下去的，天天撞到膝盖了</p><p><img src="'+O+'" alt=""></p><p>叶子笑起来粉可爱</p><p><img src="'+$+'" alt=""></p><p><img src="'+A+'" alt=""></p><p>到达沙河立交了</p><p><img src="'+B+'" alt=""></p><p><img src="'+I+'" alt=""></p><p><img src="'+M+'" alt=""></p>',51);function W(X,Y,pp,_p,sp,tp){const s=e("font");return l(),a("div",{"data-pagefind-body":!0},[q,E,z,F,J,K,t(s,{color:"red"},{default:i(()=>[_("本次活动刷街排行，仅做参考")]),_:1}),p("ol",null,[p("li",null,[t(s,{color:"red"},{default:i(()=>[_("**坤坤，53公里**")]),_:1}),_("（这家伙直接刷回下沙了）")]),p("li",null,[t(s,{color:"red"},{default:i(()=>[_("安安、草草、艾文、猫，45公里")]),_:1})]),p("li",null,[t(s,{color:"blue"},{default:i(()=>[_("**summer，42公里**")]),_:1})]),p("li",null,[t(s,{color:"blue"},{default:i(()=>[_("心灵驿站，38公里")]),_:1})]),p("li",null,[t(s,{color:"blue"},{default:i(()=>[_("叶子、树青，35公里")]),_:1})]),R,U,Z]),G,p("p",null,[p("strong",null,[t(s,{color:"blue"},{default:i(()=>[_("希望能加强时间观念，这不管在生活还是工作，都是非常重要的")]),_:1})])]),H])}const ep=o(Q,[["render",W]]);export{op as __pageData,ep as default};
