import{_ as e,o as s,c as o,j as t}from"./chunks/framework.Z1Dznsxj.js";const a="/images/2007/12/03_12781.jpg",k=JSON.parse('{"title":"怀念爷爷","description":"","frontmatter":{"title":"怀念爷爷","tags":["怀念","爷爷"],"id":991,"categories":["Life"],"abbrlink":"40236852","date":"2007-12-03T16:59:51.000Z"},"headers":[],"relativePath":"Life/怀念爷爷.md","filePath":"Life/怀念爷爷.md","lastUpdated":1719711816000}'),_={name:"Life/怀念爷爷.md"},n=t("p",null,"很想写篇关于爷爷的文章，很想让所有人知道我有个好爷爷，很想把爷爷生平的光辉历史写出来，可是，不管在我眼里、心里，爷爷是多么的伟大，在其他人眼里，爷爷也不过是位普通的老人。",-1),i=t("p",null,"爷爷很仁慈，我长这么大没见他发过脾气，在他的仁慈施教下，我们整个家族都继承了这一优良传统，家里从未发生口角等纠纷。爷爷生于20年代，跟大多数老一辈人，经历了抗战、解放、文革、下乡等历史时期。爷爷生活很朴素，年轻时过得很苦，后来生活逐渐好了，也依旧保持勤俭生活，子女想给他买衣服他都不让买，宁愿一件衣服穿个几十年。",-1),c=t("p",null,"翻遍硬盘发现唯一一张跟爷爷的合影是大概10年前拍的，还是很小的扫描件。这几年在外读书工作，陪爷爷的时间很少，很想再陪爷爷喝茶聊天，却已人去楼空。",-1),d=t("p",null,"写到这里我已泪流满面。",-1),r=t("p",null,"07年11月10日，农历十月初一，爷爷去世了，享年84岁。",-1),l=t("p",null,[t("img",{src:a,alt:""})],-1),p=[n,i,c,d,r,l];function f(m,h,u,g,L,$){return s(),o("div",{"data-pagefind-body":!0},p)}const x=e(_,[["render",f]]);export{k as __pageData,x as default};
