import{_ as t,c as s,o,j as e}from"./chunks/framework.H1k9SCod.js";const b=JSON.parse('{"title":"雨夜里独上南山","description":"","frontmatter":{"title":"雨夜里独上南山","tags":["南山","户外","登山"],"id":1044,"categories":["Life"],"abbrlink":"318a0e4","date":"2008-08-12T08:33:48.000Z"},"headers":[],"relativePath":"Life/雨夜里独上南山.md","filePath":"Life/雨夜里独上南山.md","lastUpdated":1719711816000}'),a={name:"Life/雨夜里独上南山.md"},_=e("p",null,"心情跟乌云笼罩下的深圳一样，低沉而压抑。",-1),n=e("p",null,"（昨天）下班了，雨还在下，坐上81路，经过科技园，经过深大北，站台人很多，不想转车，于是一路坐到南园村，然后转了77到蛇口沃尔玛。下车走了不久鞋子就湿了，依然从码头登山口进去，依然走台阶，不同的是这次是独自一人。",-1),i=e("p",null,"19点上山，还看得见路，鞋子进了水，有点打滑，一路都是一个人，台阶到处是乱蹦的癞蛤蟆，上了十来分钟后雨基本停了，收了雨伞，只戴了帽子，一路迷雾，后无退路，前进。快到1号亭时看到山下面已经灯火阑珊，没有休息，打开小手电继续爬，在28分钟时候到达福寿亭，然后看到前面的路封了，没办法只好往回撤。",-1),d=e("p",null,"下了好久的台阶，真的感觉好久，一看表，下山还不到10分钟，没有月光，整条路都是黑的，一路只有蛐蛐声，偶尔身后会有脚步声，转身一看，啥都没有，那个寒啊。",-1),c=e("p",null,"从进登山口到出，总共52分钟，路上遇到7个人，2个是情侣，1个是单独MM（真佩服），1个在亭上大叫的GG，1个是巡查的管理人员。",-1),l=e("p",null,"重要的不是选择了哪条路，而是在自己选择的路上能走多远，爬山如此，生活如此。",-1),r=[_,n,i,d,c,l];function p(f,h,u,m,g,L){return o(),s("div",{"data-pagefind-body":!0},r)}const k=t(a,[["render",p]]);export{b as __pageData,k as default};
